        </div>

        <div className="products-section">
          {
            <FilterBox
              count={count}
              activeFilter={isActive1}
              activeSorting={isActive2}
              clearFilter={handleClearFilter}
              index={index}
              category1={categoryJackets}
              changeCategory1={handleChangeCheckedJackets}
