import React from "react";
import styled from "styled-components";

function Button({ icon, count, link }) {
	return (
		<Wrapper href={ link } className="navbar-button">
      <img src={ icon } alt="navbar button icon" className="navbar-button-image" />
      <span className="navbar-button-count">{ count }</span>
		</Wrapper>
	);
}

const Wrapper = styled.a`
	// position: fixed;
	display: flex;
  justify-content: space-between;
	align-items: center;
	height: 5rem;
	background: var(--clr-white);
	cu

	.navbar_catalog {
		display: flex;
    align-items: center;
	}

  .navbar-left_bar {
    display: flex;
    align-items: center;
    height: 100%;
  }

  .navbar_catalog-name {
    font-size: 16px;
  }

  .navbar_catalog-name:hover {
    color: var(--clr-primary-2);
    text-decoration: underline;
  }
`;

export default Button;
